 p_id=>wwv_flow_imp.id(38919782365814177045)
,p_install_id=>wwv_flow_imp.id(38919637401717169842)
,p_name=>'fileparser'
,p_sequence=>10
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace package fileparser authid definer as',
'',
'--------------------------------------------------------------------------------',
'',
'-- DO NOT CHANGE TYPE WITHOUT ALIGNING CURSOR IN PACKAGE BODY FUNCTION view_data_raw',
'type data_raw_row is record (',
'    line_number    number,',
'    --',
'    my_string      varchar2 (4000 byte),',
'    my_number      varchar2 (  50 byte),',
'    my_date        varchar2 (  50 byte) );',
'',
'type data_raw_tab is table of data_raw_row;',
'',
'-----',
'',
'type data_row is record (',
'    line_number    number,',
'    --',
'    my_string      varchar2 (  50 byte),',
'    my_number      number,',
'    my_number_txt  varchar2 (  50 byte),',
'    my_date        date,',
'    my_date_txt    varchar2 (  50 byte),',
'    --',
'    errors         number,',
'    warnings       number,',
'    info           number,',
'    messages       varchar2 (4000 byte) );',
'',
'type data_tab is table of data_row;',
'',
'--------------------------------------------------------------------------------',
'',
'function view_data_raw (',
'    p_content in clob ) ',
'    return data_raw_tab pipelined;',
'',
'--------------------------------------------------------------------------------',
'',
'function view_data (',
'    p_content in clob ) ',
'    return data_tab pipelined;',
'',
'--------------------------------------------------------------------------------',
'',
'function to_blob (',
'    p_clob            in clob,',
'    p_charset_id       in integer default dbms_lob.default_csid,',
'    p_error_on_warning in boolean default false )',
'    return blob;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_row_delimiter (',
'    p_content in clob )',
'    return varchar2;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_first_row (',
'    p_content       in clob,',
'    p_row_delimiter in varchar2 )',
'    return varchar2;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_second_row (',
'    p_content       in clob,',
'    p_row_delimiter in varchar2 )',
'    return varchar2;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_col_delimiter (',
'    p_header_row in varchar2 )',
'    return varchar2;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_skip_rows (',
'    p_second_row in varchar2 )',
'    return number;',
'',
'--------------------------------------------------------------------------------',
'',
'end fileparser;',
'/',
'',
'',
'create or replace package body fileparser as',
'',
'--------------------------------------------------------------------------------',
'',
'c_tab  constant varchar2 (1 byte) := chr(9);',
'c_lf   constant varchar2 (1 byte) := chr(10);',
'c_crlf constant varchar2 (2 byte) := chr(13)||chr(10);',
'',
'--------------------------------------------------------------------------------',
'',
'function view_data_raw (',
'    p_content in clob )',
'    return data_raw_tab pipelined ',
'is',
'    l_row_delimiter varchar2(2);',
'    l_col_delimiter varchar2(1);',
'    l_first_row     varchar2(32767);',
'    l_second_row    varchar2(32767);',
'    l_skip_rows     number;',
'    l_blob          blob;',
'    l_data          data_raw_tab;',
'    l_query         varchar2(32767) := q''{',
'        -- Do not change this query without aligning record type data_raw_row ',
'        -- in package spec!',
'        select line_number,',
'               substrb( trim( #MY_STRING# ), 1, 4000) as my_string,',
'               substrb( trim( #MY_NUMBER# ), 1,   50) as my_number,',
'               substrb( trim( #MY_DATE#   ), 1,   50) as my_date',
'          from table ( apex_data_parser.parse (',
'                         p_file_name         => ''xxx.csv'',',
'                         p_csv_col_delimiter => :col_delimiter,',
'                         p_content           => :content,',
'                         p_skip_rows         => :skip_rows ) )',
'      }'';',
'    ',
'    --------------------',
'',
'    cursor discover_columns ( p_content in blob ) is',
'        select column_position,',
'               column_name,',
'               ''col'' || lpad( to_char( column_position ), 3, ''0'' )',
'               as parser_column',
'          from table ( apex_data_parser.get_columns ( ',
'                           apex_data_parser.discover (',
'                               p_content           => p_content,',
'                               p_file_name         => ''xxx.csv'',',
'                               p_csv_col_delimiter => l_col_delimiter,',
'                               p_max_rows          => 1 ) ) );',
'',
'    --------------------',
'',
'begin',
'    if p_content is not null then',
'        ',
'        l_row_delimiter := get_row_delimiter ( p_content                      );',
'        l_first_row     := get_first_row     ( p_content    , l_row_delimiter );',
'        l_second_row    := get_second_row    ( p_content    , l_row_delimiter );',
'        l_col_delimiter := get_col_delimiter ( l_first_row                    );',
'        l_skip_rows     := get_skip_rows     ( l_second_row                   );',
'        l_blob          := to_blob           ( p_content                      );',
'',
'        -- prepare query - found columns',
'        for i in discover_columns ( l_blob ) loop',
'            l_query := replace( l_query,',
'                                ''#'' || i.column_name || ''#'',',
'                                i.parser_column );',
'        end loop;',
'',
'        -- prepare query - not found columns',
'        l_query := regexp_replace( l_query, ''#\w+#'', ''null'' );',
'',
'        -- depending on your use case you should consider to use the limit clause',
'        execute immediate l_query bulk collect into l_data ',
'            using ',
'                l_col_delimiter,',
'                l_blob,',
'                l_skip_rows;',
'',
'        if l_data.count > 0 then',
'            for i in 1 .. l_data.count loop',
'                pipe row ( l_data(i) );',
'            end loop;',
'        end if;',
'',
'    end if;',
'',
'exception',
'    when others then',
'        if discover_columns%isopen then',
'            close discover_columns;',
'        end if;',
'        raise;',
'end view_data_raw;',
'',
'--------------------------------------------------------------------------------',
'',
'function view_data (',
'    p_content in clob ) ',
'    return data_tab pipelined',
'is',
'    l_row             data_row;',
'    l_number_errors   pls_integer;',
'    l_number_warnings pls_integer;',
'    l_number_info     pls_integer;',
'    l_errors          varchar2(32767);',
'    l_warnings        varchar2(32767);',
'    l_info            varchar2(32767);',
'',
'    --------------------',
'',
'    cursor file_data ( p_content clob ) is',
'    select line_number,',
'           my_string,',
'           to_number( my_number',
'                      default null on conversion error ) as my_number,',
'           my_number                                     as my_number_txt,',
'           to_date( my_date ',
'                    default null on conversion error,',
'                    ''yyyy-mm-dd'')                        as my_date,',
'           my_date                                       as my_date_txt',
'      from fileparser.view_data_raw ( p_content );',
'',
'    --------------------',
'',
'    procedure add_error (p_text varchar2) is',
'    begin',
'        l_number_errors := l_number_errors + 1;',
'        l_errors := l_errors || ''ERROR: '' || p_text || c_lf;',
'    end add_error;',
'',
'    --------------------',
'',
'    procedure add_warning (p_text varchar2) is',
'    begin',
'        l_number_warnings := l_number_warnings + 1;',
'        l_warnings := l_warnings || ''WARNING: '' || p_text || c_lf;',
'    end add_warning;',
'',
'    --------------------',
'',
'    procedure add_info (p_text varchar2) is',
'    begin',
'        l_number_info := l_number_info + 1;',
'        l_info := l_info || ''INFO: '' || p_text || c_lf;',
'    end add_info;',
'',
'    --------------------',
'',
'  begin',
'',
'    if p_content is not null then',
'',
'      for i in file_data ( p_content )',
'      loop',
'',
'        l_number_errors   := 0;',
'        l_number_warnings := 0;',
'        l_number_info     := 0;',
'        l_errors          := null;',
'        l_warnings        := null;',
'        l_info            := null;',
'        l_row             := null;',
'',
'        l_row.line_number   := i.line_number;',
'        l_row.my_string     := i.my_string;',
'        l_row.my_number     := i.my_number;',
'        l_row.my_number_txt := i.my_number_txt;',
'        l_row.my_date       := i.my_date;',
'        l_row.my_date_txt   := i.my_date_txt;',
'',
'        if i.my_number_txt is not null and i.my_number is null then',
'          add_error(''Unable to convert my_number.'');',
'        end if;',
'',
'        if i.my_date_txt is not null and i.my_date is null then',
'          add_error(''Unable to convert my_date.'');',
'        end if;',
'',
'        if i.my_date_txt is null then',
'          add_warning(''My_date is null.'');',
'        end if;',
'',
'        if i.my_string is null then',
'          add_info(''My_string is null.'');',
'        end if;',
'',
'        -- more checks here...',
'  ',
'        l_row.errors   := l_number_errors;',
'        l_row.warnings := l_number_warnings;',
'        l_row.info     := l_number_info;',
'        l_row.messages := substrb(l_errors || l_warnings || l_info, 1, 4000);',
'',
'        pipe row (l_row);',
'',
'      end loop;',
'',
'    end if;',
'',
'exception',
'    when others then',
'        if file_data%isopen then',
'            close file_data;',
'        end if;',
'        raise;',
'end view_data;',
'',
'--------------------------------------------------------------------------------',
'',
'function to_blob (',
'    p_clob            in clob,',
'    p_charset_id       in integer default dbms_lob.default_csid,',
'    p_error_on_warning in boolean default false )',
'    return blob',
'is',
'    l_result       blob;',
'    l_dest_offset  integer := 1;',
'    l_src_offset   integer := 1;',
'    l_lang_context integer := dbms_lob.default_lang_ctx;',
'    l_warning      integer;',
'    l_warning_msg  varchar2(50);',
'begin',
'    dbms_lob.createtemporary (',
'        lob_loc => l_result,',
'        cache   => true );',
'',
'    dbms_lob.converttoblob (',
'        dest_lob     => l_result,',
'        src_clob     => p_clob,',
'        amount       => length( p_clob ),',
'        dest_offset  => l_dest_offset,',
'        src_offset   => l_src_offset,',
'        blob_csid    => p_charset_id,',
'        lang_context => l_lang_context,',
'        warning      => l_warning );',
'',
'    if l_warning != dbms_lob.no_warning then',
'',
'        if l_warning = dbms_lob.warn_inconvertible_char then',
'            l_warning_msg := ''warning: inconvertible character.'';',
'        else',
'            l_warning_msg := ''warning: ('' || l_warning || '') during clob conversion.'';',
'        end if;',
'',
'        if p_error_on_warning then',
'            raise_application_error (',
'                -20567, -- random value between -20000 and -20999',
'                l_warning_msg );',
'        else',
'            dbms_output.put_line( l_warning_msg );',
'        end if;',
'',
'    end if;',
'',
'    return l_result;',
'end to_blob;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_row_delimiter (',
'    p_content in clob )',
'    return varchar2',
'is',
'    l_row_delimiter varchar2(2);',
'begin',
'    if instr(p_content, c_crlf) > 0 then',
'        l_row_delimiter := c_crlf;',
'    elsif instr(p_content, c_lf) > 0 then',
'        l_row_delimiter := c_lf;',
'    else',
'        raise_application_error(-20999, ''Unable to determine row delimiter'');',
'    end if;',
'    return l_row_delimiter;',
'end get_row_delimiter;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_first_row (',
'    p_content       in clob,',
'    p_row_delimiter in varchar2 )',
'    return varchar2',
'is',
'begin',
'    return substr (',
'        p_content, ',
'        1, ',
'        least(instr(p_content, p_row_delimiter, 1, 1)-1, 32767) );',
'end get_first_row;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_second_row (',
'    p_content       in clob,',
'    p_row_delimiter in varchar2 )',
'    return varchar2',
'is',
'    l_first number;',
'begin',
'    l_first := instr(p_content, p_row_delimiter) + length(p_row_delimiter);',
'    return substr(',
'        p_content, ',
'        l_first,',
'        least(instr(p_content, p_row_delimiter, l_first) -l_first, 32767) );',
'end get_second_row;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_col_delimiter (',
'    p_header_row in varchar2 )',
'    return varchar2',
'is',
'    l_col_delimiter varchar2(1);',
'begin',
'    if    instr(p_header_row, '','')   > 0 then l_col_delimiter := '','';  ',
'    elsif instr(p_header_row, '';'')   > 0 then l_col_delimiter := '';'';  ',
'    elsif instr(p_header_row, ''#'')   > 0 then l_col_delimiter := ''#'';  ',
'    elsif instr(p_header_row, ''|'')   > 0 then l_col_delimiter := ''|'';  ',
'    elsif instr(p_header_row, c_tab) > 0 then l_col_delimiter := c_tab;',
'    else  raise_application_error(-20999, ''Unable to determine column delimiter'');',
'    end if;',
'    return l_col_delimiter;',
'end get_col_delimiter;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_skip_rows (',
'    p_second_row in varchar2 )',
'    return number',
'is',
'    l_skip_rows number := 2;',
'    l_characters apex_t_varchar2;',
'begin',
'    l_characters := apex_string.split(',
'                        p_str => p_second_row,',
'                        p_sep => null );',
'',
'    for i in 1 .. l_characters.count ',
'    loop',
'        /*',
'        We check for Markdown table header style characters, which are',
'        separating the data from the header:',
'',
'       | my_string | my_number | my_date    |',
'       | --------- | --------: | :--------: |',
'       | one       |   123.456 | 2022-08-11 |',
'       | two       |    10.361 | unknown    |',
'       | three     |   NaN     | 2022-02-31 |',
'       | four      |     5.957 | 2022-08-09 |',
'       | five      |    78,630 |            |',
'        */',
'        if l_characters(i) not in (''|'', ''-'', '' '', '':'') then',
'            l_skip_rows := 1;',
'        end if;',
'    end loop;',
'    return l_skip_rows;',
'end get_skip_rows;',
'',
'--------------------------------------------------------------------------------',
'',
'end fileparser;',
'/',
''))
);
end;
